import React from 'react'
const Header = (props) => {
  return (
    <div>
	<h1>{props.course}</h1>
    </div>
  )
}
const Part = (props) => {
  return (
    <div>
	<p>
	{props.name} {props.exercise}
	</p>
    </div>
  )
}
const Total = (props) => {
  return (
    <div>
	<p>{props.part.exercises[0]+props.part.exercises[1]+props.part.exercises[2]}</p>
    </div>
  )
}
const Content = (props)=> {
  return (
    <div>
	    <p>
		<Part name={props.part.name[0]} exercise={props.part.exercises[0]}/>
		<Part name={props.part.name[1]} exercise={props.part.exercises[1]}/>
		<Part name={props.part.name[2]} exercise={props.part.exercises[2]}/>
		</p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part = [{
    name: 'Fundamentals of React',
    exercises: 10
  },
  {
    name: 'Using props to pass data',
    exercises: 7
  },
  {
    name: 'State of a component',
    exercises: 14
  }]




  return (
    <div>
      <Header course={course} />
      <Content part={part}/>
      <Total exe={part}/>
    </div>
  )
}
export default App;
